import React, { useState, useEffect } from 'react';
import api from './services/api';

import'./global.css';
import'./app.css';
import'./SeiderBar.css';
import'./Main.css';



function App() {

  const [github_username, setGithubusername] = useState('');
  const [techs, setTechs] = useState('');


  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;

        setLatitude(latitude);
        setLongitude(longitude);
      },
      (err) => {
        console.log(err);
      },
      {
        timeout: 30000,
      }
    )
  }, []);

  async function handleAddDev (e) {
    e.preventDefault();

    const response = await api.post('/devs', {
      github_username,
      techs,
      latitude,
      longitude,
    })
    console.log(response.data);

  }

  return (
    <div id="app">
      <aside>
          <strong>Cadastrar</strong>
          <form onSubmit={handleAddDev}>
            <div className="input-block">
              <label htmlfor="github_username">Usuario do Github</label>
              <input 
                name="github_username" 
                id="username_github" 
                require 
                value = { github_username}
                onChange ={e => setGithubusername(e.target.value)}
              />
            </div>
            <div className="input-block">
                <label htmlfor="techs">Usuario do Github</label>
                <input 
                  name="techs" 
                  id="techs" 
                  require 
                  value = {techs}
                  onChange={e => setTechs(e.target.value)}
                />
            </div> 

            <div className="input-group">
              <div className="input-block">
                  <label htmlfor="latitude">latitude</label>
                  <input 
                    type="number" 
                    name="latitude" 
                    id="latitude" 
                    require 
                    value={latitude}
                    onChange={e => setLatitude(e.target.value)} 
                  />
              </div> 

              <div className="input-block">
                <label htmlfor="longitude">longitude</label>
                <input 
                  type="number" 
                  name="longitude" 
                  id="longitude" 
                  require 
                  value={longitude}
                  onChange={e => setLongitude(e.target.value)}
                />
              </div>                
            </div>

            <button type="submit">Salvar</button>

          </form>
      </aside>

      <main>
        <ul>
          <li className="dev-item">
            <header>
              <img src="https://avatars1.githubusercontent.com/u/2254731?s=400&v=4" alt="diego fernandes"/>
              <div className="user-info">
                <strong>Diego Fernandes</strong>
                <span>ReactJs, React Native, Node.js</span>

              </div>
            </header>
            <p>CTO na @Rocketseat. Apaixonado pelas melhores tecnologias de desenvolvimento web e mobile.</p>
            <a href="https://github.com/diego3g">Acessar perfil no Github</a>
          </li>

          <li className="dev-item">
            <header>
              <img src="https://avatars1.githubusercontent.com/u/2254731?s=400&v=4" alt="diego fernandes"/>
              <div className="user-info">
                <strong>Diego Fernandes</strong>
                <span>ReactJs, React Native, Node.js</span>

              </div>
            </header>
            <p>CTO na @Rocketseat. Apaixonado pelas melhores tecnologias de desenvolvimento web e mobile.</p>
            <a href="https://github.com/diego3g">Acessar perfil no Github</a>
          </li>

          <li className="dev-item">
            <header>
              <img src="https://avatars1.githubusercontent.com/u/2254731?s=400&v=4" alt="diego fernandes"/>
              <div className="user-info">
                <strong>Diego Fernandes</strong>
                <span>ReactJs, React Native, Node.js</span>

              </div>
            </header>
            <p>CTO na @Rocketseat. Apaixonado pelas melhores tecnologias de desenvolvimento web e mobile.</p>
            <a href="https://github.com/diego3g">Acessar perfil no Github</a>
          </li>

          <li className="dev-item">
            <header>
              <img src="https://avatars1.githubusercontent.com/u/2254731?s=400&v=4" alt="diego fernandes"/>
              <div className="user-info">
                <strong>Diego Fernandes</strong>
                <span>ReactJs, React Native, Node.js</span>

              </div>
            </header>
            <p>CTO na @Rocketseat. Apaixonado pelas melhores tecnologias de desenvolvimento web e mobile.</p>
            <a href="https://github.com/diego3g">Acessar perfil no Github</a>
          </li>

        </ul>
      </main>
    </div>
  );
}

export default App;
